sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {

    var notificationInfo = JSON.parse(event.parm2);


	var link = '<a href="'+ gs.getProperty("glide.servlet.uri") + 'sp?id=avaliacao_pex&sys_id=' + current.sys_id +'">' + gs.getMessage("clique aqui") + '</a>';

	template.print("Você possui somente " + notificationInfo.days + " dia(s) para responder a avaliação que se encontra disponível no portal de serviço, para acessá-la " + link);

})(current, template, email, email_action, event);
