sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {
	// Target = Flight Crew [x_alabs_pex_flight_crew]
	//var gr = new GlideRecord('sys_user');
	//gr.addEncodedQuery('employee_number='+source.u_re);
	//gr.query();

	var gr;

	//Recupera vÃ´os
	function ConverterDataHora(data) {
		var fullDate = data.split(" ");
		var dateFormat = fullDate[0]
			.split("-")
			.reverse()
			.join("-");
		var finalDate = dateFormat + " " + fullDate[1];
		return finalDate;
	}

	gr = new GlideRecord("x_alabs_pex_flights");
	gr.addEncodedQuery("numero_do_voo=" + source.u_numero_voo.toString());
	gr.query();

	if (gr.next()) {
		var flightInt = gr.sys_id.toString();

		gr = new GlideRecord("sys_user");
		gr.addEncodedQuery(
			"employee_number=" +
				source.u_re.toString() +
				"^ORemail=" +
				source.u_email.toString()
		);

		gr.query();

		if (gr.next()) {
			target.flight = flightInt;
			target.user = gr.sys_id.toString();
			target.crewman_re = gr.employee_number.toString();

			target.user_title = source.u_cargo;
			target.data_de_partida = source.u_dtt_partida;
			target.data_hora_de_partida_utc = ConverterDataHora(source.u_dtt_partida);
			target.origem = source.u_origem;
			target.destino = source.u_destino;
			target.numero_do_voo = source.u_numero_voo;
		}
	}
})(source, target, map, log, action === "update");
